--
-- Name: dbf_impact_add_to_rels_decoded_string(character varying, character varying, date, date, integer, integer, integer, text); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_add_to_rels_decoded_string(input_table_name character varying, input_id_value character varying, input_start_date date, input_end_date date, html_output integer, expand_details integer, curr_item integer, input_string text) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  decode text;
  lookup_string text not null default '';
  newline constant varchar(1) default E'\n';
  start_date varchar(4);
  end_date varchar(4);
  list_item_marker varchar(6) not null := dbf_impact_get_constant_value( 'list_item_marker' );
begin
  lookup_string = coalesce( input_string, '' );

  if lookup_string > '' then
    if html_output = 1 then
      if curr_item = 2 then -- put list-item markers round the first item in the list
        lookup_string := '<li>' || lookup_string || '</li>';
      end if;
      lookup_string := lookup_string || '<li>';
    else
      lookup_string := lookup_string || list_item_marker;
    end if;
  end if;

  decode = dbf_impact_decode( input_table_name, input_id_value, 0,  -- don't suppress links
                                  expand_details );  -- the 'expand details' flag is only relevant to selected tables

  decode = coalesce( decode, '' );

  if input_start_date is not null and input_end_date is not null then
    start_date = to_char( input_start_date, 'YYYY' );
    end_date = to_char( input_end_date, 'YYYY' );
    if start_date = end_date then
      decode = start_date || ': ' || decode;
    else
      decode = start_date || '-' || end_date || ': ' || decode;
    end if;

  elsif input_start_date is not null then
    start_date = to_char( input_start_date, 'YYYY' );
    decode = 'From ' || start_date || ': ' || decode;

  elsif input_end_date is not null then
    end_date = to_char( input_end_date, 'YYYY' );
    decode = 'To ' || end_date || ': ' || decode;
  end if;

  lookup_string := lookup_string || decode;

  if html_output = 1 and curr_item > 1 then
    lookup_string := lookup_string || '</li>';
  end if;

  return lookup_string;
end;


$$;


ALTER FUNCTION public.dbf_impact_add_to_rels_decoded_string(input_table_name character varying, input_id_value character varying, input_start_date date, input_end_date date, html_output integer, expand_details integer, curr_item integer, input_string text) OWNER TO postgres;

