    if TG_OP = 'UPDATE' then 
      perform dbf_impact_audit_literal_update( 'impact_work', 
        new.work_id, 
        new.iwork_id, 
        new.description, 
        'title_of_work_uncertain', 
        new.title_of_work_uncertain::text, 
        old.title_of_work_uncertain::text ); 
    end if;

    if TG_OP = 'INSERT'  then 
      if new.title_of_work_uncertain = 1 then
        perform dbf_impact_audit_literal_insert( 'impact_work', 
                                               new.work_id, 
                                               new.iwork_id, 
                                               new.description, 
                                               'title_of_work_uncertain', 
                                               new.title_of_work_uncertain::text ); 
      end if;
    end if;

    -- impact_work: title unknown
    if TG_OP = 'UPDATE' then 
      perform dbf_impact_audit_literal_update( 'impact_work', 
        new.work_id, 
        new.iwork_id, 
        new.description, 
        'title_of_work_unknown', 
        new.title_of_work_unknown::text, 
        old.title_of_work_unknown::text ); 
    end if;

    if TG_OP = 'INSERT' then 
      if new.title_of_work_unknown = 1 then 
        perform dbf_impact_audit_literal_insert( 'impact_work', 
          new.work_id, 
          new.iwork_id, 
          new.description, 
          'title_of_work_unknown', 
          new.title_of_work_unknown::text ); 
      end if;
    end if;
  end if; -- End of impact_work

-------------------

  if TG_RELNAME = 'INSERT' or TG_RELNAME = 'UPDATE' then
    return new;
  else
    return old;
  end if;
end;
$$;


