--
-- Name: dbf_impact_cascade01_work_changes(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_cascade01_work_changes() RETURNS trigger
    LANGUAGE plpgsql
    AS $$

begin

  if TG_OP = 'INSERT' then
    insert into impact_queryable_work( work_id, iwork_id ) values ( new.work_id, new.iwork_id );
  end if;

  if TG_OP = 'INSERT' or TG_OP = 'UPDATE' then
    perform dbf_impact_refresh_work_desc( new.work_id );
    perform dbf_impact_refresh_queryable_work( new.work_id );
    return new;

  elsif TG_OP = 'DELETE' then
    delete from impact_queryable_work where work_id = old.work_id;
    delete from impact_relationship where left_table_name  = 'impact_work' and left_id_value  = old.work_id;
    delete from impact_relationship where right_table_name = 'impact_work' and right_id_value = old.work_id;
    return old;
  end if;
end;

$$;


