--

CREATE FUNCTION dbf_impact_create_sql_user(input_username character varying, grant_edit_role character varying) RETURNS character varying
    LANGUAGE plpgsql STRICT SECURITY DEFINER
    AS $$

declare
  statement varchar(500);
  existing_user varchar(30);
begin
  if substr( input_username, 1, length( 'impact' )) != 'impact' then
    raise exception 'Username must begin with %', 'impact';
  end if;

  select usename
  into existing_user
  from pg_user
  where usename = input_username;

  if FOUND then
    null; -- user already exists in another database
  else
    statement = 'create user ' || input_username;
    execute statement;
  end if;

  statement = 'grant viewer_role_' || 'impact' || ' to ' || input_username;
  execute statement;

  if grant_edit_role = 'Y' then
    statement = 'grant editor_role_' || 'impact' || ' to ' || input_username;
  end if;
  execute statement;

  return input_username;
end;

$$;


