--

CREATE FUNCTION dbf_impact_create_user(input_username character varying, input_password character varying, input_surname character varying, input_forename character varying, input_email text, grant_edit_role character varying) RETURNS character varying
    LANGUAGE plpgsql STRICT SECURITY DEFINER
    AS $$

declare
  existing_username varchar(30);
begin
  if length( input_username ) <= length( 'impact' ) then
    raise exception 'Invalid username.';
  end if;

  select username 
  into existing_username
  from impact_users
  where trim(lower( username )) = trim(lower( input_username ));

  if FOUND then
    raise exception 'Invalid username.';
  end if;

  if input_password is null or trim( input_password ) = '' 
  or input_password = md5('') or input_password = md5( ' ' )
  or length( input_password ) != length( md5( ' ' )) -- try and check that the password is already in md5
  then
    raise exception 'Invalid password.';
  end if;

  if input_surname is null or trim( input_surname ) = '' then
    raise exception 'Surname cannot be blank.';
  end if;

  insert into impact_USERS (
    username,
    pw,
    surname,
    forename,
    email
  )
  values (
    input_username,
    input_password,
    input_surname,
    coalesce( input_forename, '' ),
    input_email
  );

  return dbf_impact_create_sql_user( input_username, grant_edit_role );
end;

$$;


