--

CREATE FUNCTION dbf_impact_decode(table_name character varying, key_value character varying, suppress_links integer, expand_details integer) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  statement text;
  decode text;
  newline constant varchar(1) := E'\n';
begin
  decode = '';

  if table_name = 'impact_person' then
    return dbf_impact_decode_person( key_value, suppress_links, expand_details );

  elsif table_name = 'impact_work' or table_name = 'impact_queryable_work' then
    return dbf_impact_decode_work( key_value, suppress_links );

  elsif table_name = 'impact_manifestation' then
    return dbf_impact_decode_manifestation( key_value, suppress_links );

  elsif table_name = 'impact_nisba' then -- in practice only used by IMPAcT
    return dbf_impact_decode_nisba( key_value::integer, suppress_links );

  elsif table_name = 'impact_location' then
    if expand_details = 1 then
      statement = 'select location_name || case when location_synonyms > '''' then '
                || ' ''' || newline || '('' || location_synonyms || '')'' else '''' end '
                || ' from ' || table_name || ' where location_id = ' || key_value;
    else
      statement = 'select location_name from ' || table_name || ' where location_id = ' || key_value;
    end if;

  elsif table_name = 'impact_relationship_type' then
    statement = 'select desc_left_to_right from ' || table_name 
              || ' where relationship_code = ''' || key_value || '''';

  elsif table_name = 'impact_comment' then
    statement = 'select comment from ' || table_name || ' where comment_id = ' || key_value;

  elsif table_name = 'impact_event' then
    statement = 'select event_desc from ' || table_name || ' where event_id = ' || key_value;

  elsif table_name = 'impact_image' then
    statement = 'select image_filename from ' || table_name || ' where image_id = ' || key_value;

  elsif table_name = 'impact_institution' then
    statement = 'select institution_name from ' || table_name || ' where institution_id = ' || key_value;

  elsif table_name = 'impact_resource' then
    statement = 'select resource_name from ' || table_name || ' where resource_id = ' || key_value;

  elsif table_name = 'impact_publication' then
    statement = 'select publication_details from ' || table_name || ' where publication_id = ' || key_value;

  elsif table_name = 'impact_subject' then
    statement = 'select subject_desc from ' || table_name || ' where subject_id = ' || key_value;

  elsif table_name = 'impact_nationality' then
    statement = 'select nationality_desc from ' || table_name || ' where nationality_id = ' || key_value;

  elsif table_name = 'impact_role_category' then
    statement = 'select role_category_desc from ' || table_name || ' where role_category_id = ' || key_value;

  else
    return 'Cannot decode ' || table_name || ' ' || key_value;
  end if;

  execute statement into decode;
  return decode;
end;

$$;


