--

CREATE FUNCTION dbf_impact_decode_manifestation(key_value character varying, suppress_links integer) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  decode text;
  m_title text;
  m_alternative_titles text;
  m_shelfmark varchar(500);
  m_repos text;
  w_authors text;
  manif_date varchar(32);
  date_inferred smallint;
  date_uncertain smallint;
  date_approx smallint;
  integer_key integer;
  work_id_var varchar(100);
  separator constant varchar(4) := ' ~ ';
  -- newline constant varchar(1) := E'\n';
begin
  decode = '';

  select w.iwork_id, w.work_id 
  into integer_key, work_id_var
  from impact_work w, impact_relationship r
  where r.left_table_name = 'impact_manifestation'
  and r.left_id_value = key_value
  and r.relationship_type = 'is_manifestation_of'
  and r.right_table_name = 'impact_work'
  and r.right_id_value = w.work_id;

  select dbf_impact_list_creators( work_id_var, 0 )
  into w_authors;

  select trim( coalesce( manifestation_title, '' )),
         trim( coalesce( manifestation_alternative_titles, '' )),
         trim( coalesce( id_number_or_shelfmark, '' )),
         to_char( manifestation_creation_date, 'YYYY' ),
         manifestation_creation_date_inferred,
         manifestation_creation_date_uncertain,
         manifestation_creation_date_approx
  into m_title, m_alternative_titles, m_shelfmark, 
       manif_date, date_inferred, date_uncertain, date_approx
  from impact_manifestation
  where manifestation_id = key_value;

  select dbf_impact_list_rels_decoded( 'impact_institution',   -- required_table 
                                     'stored_in',          -- required_reltype
                                     'impact_manifestation', -- known_table
                                     key_value,            -- known_id
                                     0 )                   -- html_output
  into m_repos;

  if w_authors > '' then
    decode = w_authors || ': ';
  end if;

  if m_title > '' then
    decode = decode || m_title;
  end if;

  if m_alternative_titles > '' then
    if m_title > '' then
      decode = decode || separator;
    end if;
    decode = decode || 'Alternative title(s): ' || m_alternative_titles;
  end if;

  if manif_date > '' and manif_date != '9999' then
    if manif_date like '0%' then
      manif_date = substr( manif_date, 2 ); -- strip off the leading zero
    end if;

    if date_approx > 0 then
      manif_date = 'c.' || manif_date;
    end if;
    if date_uncertain > 0 then
      manif_date = manif_date || '?';
    end if;
    if date_inferred > 0 then
      manif_date = '[' || manif_date || ']';
    end if;
    decode = decode || separator || ' Date: ' || manif_date;
  end if;

  if m_repos > '' then
    if decode > '' then
      decode = decode || separator;
    end if;
    decode = decode || 'Repository: ' || m_repos;
  end if;

  if m_shelfmark > '' then
    if decode > '' then
      decode = decode || separator;
    end if;
    decode = decode || 'Shelfmark: ' || m_shelfmark;
  end if;

  decode = decode || separator || 'ID ' || key_value;

  if suppress_links = 1 then
    return decode;
  end if;

  select dbf_impact_link_to_edit_app( decode, '?iwork_id=' || integer_key::varchar ) into decode;

  return decode;
end;

$$;


