--
-- Name: dbf_impact_delete_user(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_delete_user(input_username character varying) RETURNS integer
    LANGUAGE plpgsql STRICT SECURITY DEFINER
    AS $$

declare
  statement varchar(100);
  rowcount integer;
  supervisor_role constant integer := 99;
  existing_user varchar(30);
  views_recd record;
begin
  -- Drop any views belonging to this user
  for views_recd in select viewname
                    from pg_views 
                    where schemaname = 'public'
                    and viewowner = input_username
  loop
    -- Double check that the view still exists
    -- as it may have been dropped by now, as a result of an earlier "cascade"
    select count(*) into rowcount
    from pg_views
    where schemaname = 'public'
    and viewowner = input_username
    and viewname = views_recd.viewname;

    if rowcount > 0 then
      statement = 'drop view ' || views_recd.viewname || ' cascade';
      execute statement;
    end if;
  end loop;

  delete from impact_user_saved_query_selection	
  where
    query_id in ( select query_id from impact_user_saved_queries
                  where username = input_username );

  delete from impact_user_saved_queries
  where username = input_username;

  delete from impact_sessions
  where
    username = input_username;

  delete from impact_user_roles
  where
    username = input_username;

  delete from impact_users
  where
    username = input_username;
  get diagnostics rowcount = row_count;

  -- Start a new block, so that ONLY the bit in the inner block 
  -- is rolled back if an error occurs.
  begin
    select usename
    into existing_user
    from pg_user
    where usename = input_username;

    if FOUND then
      statement = 'drop user ' || input_username ;
      execute statement;
    end if;

  exception
    when dependent_objects_still_exist then
      raise notice 'Cannot drop user % as dependent objects still exist', input_username;
      return 0;
  end;

  return rowcount;
end;

$$;


