--
-- Name: dbf_impact_full_manifestation_details(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_full_manifestation_details(input_work_id character varying) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  manifestation_summary text not null default '';
  one_manif text not null default '';
  manif_count integer not null default 0;
  fieldcount integer not null default 0;
  manifestation_id_rec record;
  manifestation_row_rec record;
  enclos_id_rec record;
  repository text;
  manif_text text;
  work_text text;
  manif_date varchar(32);
  decode varchar(100);
  string_length integer;

  title_length constant integer := 100;
  quoted_text_length constant integer := 200;

  html_list_start_marker constant varchar(12) not null 
  default dbf_impact_get_constant_value('html_list_start_marker'); -- <ul><li>

  html_list_item_marker  constant varchar(12) not null 
  default dbf_impact_get_constant_value('html_list_item_marker' ); -- </li><li>

  html_list_end_marker   constant varchar(12) not null 
  default dbf_impact_get_constant_value('html_list_end_marker'  ); -- </li></ul>

  non_html_list_item_marker  constant varchar(12) not null 
  default dbf_impact_get_constant_value( 'list_item_marker'  ); --  ~ (tilde)

  newline constant varchar(1) := E'\n';
begin
  for manifestation_id_rec in 
  select * from dbf_impact_list_rel_ids ( 'impact_manifestation',  -- required table
                                        'is_manifestation_of', -- required relationship
                                        'impact_work',           -- known table 
                                        input_work_id )        -- known ID
  loop
    manif_count = manif_count + 1;
    one_manif = '';
    fieldcount = 0;
    ------------------------------------------
    -- Retrieve the data for one manifestation
    ------------------------------------------
    -- Get manifestation details
    select * from impact_manifestation 
    into manifestation_row_rec
    where manifestation_id =  manifestation_id_rec.id_value;

    -- Get repository name
    select * into repository
    from dbf_impact_list_rels_decoded( 'impact_institution', -- required table
                                     'stored_in', -- required relationship type
                                     'impact_manifestation', -- known table
                                     manifestation_id_rec.id_value, -- known ID
                                     0 );  -- HTML output

    -- Get manifestation type, decoded
    select d.document_type_desc
    into decode
    from impact_lookup_document_type d
    where d.document_type_code = manifestation_row_rec.manifestation_type;

    ---------------------------------
    -- Start to write out the details
    ---------------------------------
    if manif_count > 1 then
      manifestation_summary := manifestation_summary || html_list_item_marker;
    end if;

    manifestation_summary = manifestation_summary || '<strong>';

    -- Document type
    if decode > '' then  
      manifestation_summary = manifestation_summary || decode;
    end if;
    decode = '';

    -- Date
    manif_date = '';
    if manifestation_row_rec.manifestation_creation_date is not null then
      manif_date = to_char( manifestation_row_rec.manifestation_creation_date, 'YYYY' );
    end if;

    if manif_date > '' and manif_date != '9999' then
      if manif_date like '0%' then
        manif_date = substr( manif_date, 2 ); -- strip off the leading zero
      end if;

      if manifestation_row_rec.manifestation_creation_date_approx > 0 then
        manif_date = 'c.' || manif_date;
      end if;
      if manifestation_row_rec.manifestation_creation_date_uncertain > 0 then
        manif_date = manif_date || '?';
      end if;
      if manifestation_row_rec.manifestation_creation_date_inferred > 0 then
        manif_date = '[' || manif_date || ']';
      end if;
      decode = decode || ' dated ' || manif_date;
    end if;
    manifestation_summary := manifestation_summary || decode || '. ';

    -- Repository
    if repository > '' then
      manifestation_summary = manifestation_summary || repository;
    end if;

    -- Shelfmark
    manif_text = coalesce( manifestation_row_rec.id_number_or_shelfmark, '' );

    if repository > '' and manif_text > '' then
      manifestation_summary = manifestation_summary || ': ';
    end if;

    manifestation_summary := manifestation_summary || manif_text || newline || newline ;
    manif_text = '';

    manifestation_summary = manifestation_summary || '</strong>';

    -------------------------------------------------------------------------------------
    -- Put the remaining details for this single manifestation into a bullet-pointed list
    -------------------------------------------------------------------------------------
  
    -- Place of copying
    manif_text = '';
    select * into manif_text
    from dbf_impact_list_rels_decoded( 'impact_location', -- required table
                                     'copied_at_place', -- required relationship type
                                     'impact_manifestation', -- known table
                                     manifestation_id_rec.id_value, -- known ID
                                     0 );  -- HTML output
    if manif_text > '' then
      fieldcount = fieldcount + 1;
      if fieldcount > 1 then
        one_manif = one_manif || html_list_item_marker;
      end if;
      one_manif = one_manif || 'Place of copying: ' || manif_text;
    end if;


    -- Title
    manif_text = coalesce( manifestation_row_rec.manifestation_title, '' );
    if manif_text > '' then
      select coalesce( work_title, '' ) into work_text from impact_work where work_id = input_work_id;

      string_length = length( manif_text );
      if string_length > title_length then
        manif_text = substr( manif_text, 1, title_length );
        work_text = substr( work_text, 1, title_length );
      end if;

      if manif_text != work_text then

        -- Try to break on a space
        if string_length > title_length and strpos( manif_text, ' ' ) > 0 then
          for i in reverse length( manif_text )..1 loop
            if substr( manif_text, i, 1 ) = ' ' then
              manif_text = substr( manif_text, 1, i );
              exit;
            end if;
          end loop;
        end if;

        fieldcount = fieldcount + 1;
        if fieldcount > 1 then
          one_manif = one_manif || html_list_item_marker;
        end if;
        one_manif = one_manif || 'Title: ' || manif_text;
      end if;
    end if;

    -- Alternative title(s)
    manif_text = coalesce( manifestation_row_rec.manifestation_alternative_titles, '' );
    if manif_text > '' then
      select coalesce( work_alternative_titles, '' ) into work_text from impact_work 
      where work_id = input_work_id;

      string_length = length( manif_text );
      if string_length > title_length then
        manif_text = substr( manif_text, 1, title_length );
        work_text = substr( work_text, 1, title_length );
      end if;

      if manif_text != work_text then

        -- Try to break on a space
        if string_length > title_length and strpos( manif_text, ' ' ) > 0 then
          for i in reverse length( manif_text )..1 loop
            if substr( manif_text, i, 1 ) = ' ' then
              manif_text = substr( manif_text, 1, i );
              exit;
            end if;
          end loop;
        end if;

        fieldcount = fieldcount + 1;
        if fieldcount > 1 then
          one_manif = one_manif || html_list_item_marker;
        end if;
        one_manif = one_manif || 'Alternative title(s): ' || manif_text;
      end if;
    end if;

    -- Incipit and excipit
    manif_text = coalesce( manifestation_row_rec.manifestation_incipit, '' );
    if manif_text > '' then
      select coalesce( incipit, '' ) into work_text from impact_work where work_id = input_work_id;

      string_length = length( manif_text );
      if string_length > quoted_text_length then
        manif_text = substr( manif_text, 1, quoted_text_length );
        work_text = substr( work_text, 1, quoted_text_length );
      end if;

      if manif_text != work_text then

        -- Try to break on a space
        if string_length > quoted_text_length and strpos( manif_text, ' ' ) > 0 then
          for i in reverse length( manif_text )..1 loop
            if substr( manif_text, i, 1 ) = ' ' then
              manif_text = substr( manif_text, 1, i );
              exit;
            end if;
          end loop;
        end if;

        fieldcount = fieldcount + 1;
        if fieldcount > 1 then
          one_manif = one_manif || html_list_item_marker;
        end if;
        one_manif = one_manif || 'Incipit: ' || manif_text;
      end if;
    end if;

    manif_text = coalesce( manifestation_row_rec.manifestation_excipit, '' );
    if manif_text > '' then
      select coalesce( explicit, '' ) into work_text from impact_work where work_id = input_work_id;

      string_length = length( manif_text );
      if string_length > quoted_text_length then
        manif_text = substr( manif_text, 1, quoted_text_length );
        work_text = substr( work_text, 1, quoted_text_length );
      end if;

      if manif_text != work_text then

        -- Try to break on a space
        if string_length > quoted_text_length and strpos( manif_text, ' ' ) > 0 then
          for i in reverse length( manif_text )..1 loop
            if substr( manif_text, i, 1 ) = ' ' then
              manif_text = substr( manif_text, 1, i );
              exit;
            end if;
          end loop;
        end if;

        fieldcount = fieldcount + 1;
        if fieldcount > 1 then
          one_manif = one_manif || html_list_item_marker;
        end if;
        one_manif = one_manif || 'Explicit: ' || manif_text;
      end if;
    end if;

    -------------------------------------------
    -- Add people associated with manifestation
    -------------------------------------------
    manif_text = '';

    select dbf_impact_list_manif_people( manifestation_id_rec.id_value )
    into manif_text;

    if manif_text > '' then
      fieldcount = fieldcount + 1;
      if fieldcount > 1 then
        one_manif = one_manif || html_list_item_marker;
      end if;
      one_manif = one_manif || 'Associated people: ' || manif_text;
    end if; 

    -----------------------------------------
    -- Places where manifestation was studied
    -----------------------------------------
    manif_text = '';
    select dbf_impact_list_rels_decoded( 'impact_location',                --required_table
                                       'was_studied_in_place',         --required_reltype
                                       'impact_manifestation',           --known_table
                                       manifestation_id_rec.id_value,  --known_id
                                       1,                              --html_output 
                                       1                               --expand details
                                     ) into manif_text;

    if manif_text > '' then
      fieldcount = fieldcount + 1;
      if fieldcount > 1 then
        one_manif = one_manif || html_list_item_marker;
      end if;
      one_manif = one_manif || 'Where studied: ' || manif_text;
    end if; 


    -----------------------
    -- Add manifestation ID
    -----------------------
    fieldcount = fieldcount + 1;
    if fieldcount > 1 then
      one_manif = one_manif || html_list_item_marker;
    end if;
    one_manif = one_manif || 'ID: ' || manifestation_row_rec.manifestation_id;

    -- Enclosures in this manifestation
    select desc_right_to_left into decode
    from impact_relationship_type
    where relationship_code = 'enclosed_in';

    for enclos_id_rec in 
    select * from dbf_impact_list_lefthand_rel_ids( 'impact_manifestation', -- required table
                                                  'enclosed_in',
                                                  'impact_manifestation', -- known table 
                                                  manifestation_id_rec.id_value ) -- known ID
    loop
      fieldcount = fieldcount + 1;
      manif_text = '';
      manif_text = dbf_impact_decode( 'impact_manifestation', enclos_id_rec.id_value );
      if manif_text > '' then
        if fieldcount > 1 then
          one_manif := one_manif || html_list_item_marker;
        end if;
        one_manif := one_manif || decode || ': ' || manif_text;
      end if;
    end loop;
    decode = '';

    -- Manifestations in which this one was enclosed
    select desc_left_to_right into decode
    from impact_relationship_type
    where relationship_code = 'enclosed_in';

    for enclos_id_rec in 
    select * from dbf_impact_list_righthand_rel_ids( 'impact_manifestation', -- required table
                                                   'enclosed_in',
                                                   'impact_manifestation', -- known table 
                                                   manifestation_id_rec.id_value ) -- known ID
    loop
      fieldcount = fieldcount + 1;
      manif_text = '';
      manif_text = dbf_impact_decode( 'impact_manifestation', enclos_id_rec.id_value );
      if manif_text > '' then
        if fieldcount > 1 then
        one_manif := one_manif || html_list_item_marker;
        end if;
        one_manif := one_manif || decode || ': ' || manif_text;
      end if;
    end loop;

    if strpos( one_manif, html_list_item_marker ) > 0 then
      one_manif = html_list_start_marker || one_manif || html_list_end_marker;
    end if;

    manifestation_summary = manifestation_summary || one_manif;
  end loop;

  if manif_count > 1 then
    manifestation_summary =  dbf_impact_get_constant_value('html_olist_start_marker') 
                          || manifestation_summary 
                          || dbf_impact_get_constant_value('html_olist_end_marker');
  end if;

  manif_text = '<strong>' || manif_count::varchar || '</strong>' || ' manifestation';

  if manif_count != 1 then
    manif_text = manif_text || 's';
  end if;

  if manif_count > 0 then
    manif_text = manif_text || ':' || newline || newline;
  end if;

  manifestation_summary = manif_text || manifestation_summary;
  return manifestation_summary;
end;


$$;


