--
-- Name: dbf_impact_get_work_desc(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_get_work_desc(input_work_id character varying) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  work_desc text not null = '';
  workrec record;
  misc text;
  max_title_length constant integer := 100;
  newline constant varchar(1) := E'\n';
begin
  work_desc = '';

  ------------------------------------------------
  -- Work title (use incipit if there is no title)
  ------------------------------------------------
  select * into workrec from impact_work where work_id = input_work_id;

  if workrec.work_title > '' then
    if length( workrec.work_title ) > max_title_length then
      work_desc = substr( workrec.work_title, 1, max_title_length ) || '...';
    else
      work_desc = workrec.work_title;
    end if;

  elsif workrec.incipit > '' then
    if length( workrec.incipit ) > max_title_length then
      work_desc = substr( replace( workrec.incipit, newline, ' ' ), 1, max_title_length ) || '...';
    else
      work_desc = replace( workrec.incipit, newline, ' ' );
    end if;

  else
    work_desc = 'Untitled';
  end if;
  work_desc = work_desc || ' ';

  ----------------------------
  -- Authors, commentators etc
  ----------------------------
  select dbf_impact_list_creators( input_work_id, 0 ) into misc;
  if misc > '' then
    work_desc = work_desc || ' by ' || misc || ' ';
  end if;

  ---------------
  -- Type of work
  ---------------
  select coalesce( work_type_desc, '' ) into misc from impact_work_type
  where work_type_id = workrec.work_type;

  if misc = '' or misc is null then
    misc = 'type of work not entered';
  end if;

  work_desc = work_desc || '(' || misc;

  ---------------------
  -- Subject(s) of work
  ---------------------
  select dbf_impact_list_subjects_of_work( input_work_id ) into misc;

  if misc > '' then
    work_desc = work_desc || ' on ' || misc;
  end if;


  ---------------
  -- Date of work
  ---------------
  if workrec.date_of_work_std_year is not null or workrec.date_of_work2_std_year is not null then
    work_desc = work_desc || ', dated ';

    if workrec.date_of_work_std_year is not null and workrec.date_of_work2_std_year is not null then
      misc = workrec.date_of_work_std_year::text || '-' || workrec.date_of_work2_std_year::text;

    elsif workrec.date_of_work2_std_year is not null then
      misc = workrec.date_of_work2_std_year::text || ' or before';

    elsif workrec.date_of_work_std_is_range > 0 and workrec.date_of_work_std_year is not null then
      misc = workrec.date_of_work_std_year::text || ' or after';

    else
      misc = workrec.date_of_work_std_year::text;
    end if;

    if workrec.date_of_work_approx > 0 then
      misc = 'c.' || misc;
    end if;

    if workrec.date_of_work_uncertain > 0 then
      misc = misc || '?';
    end if;

    if workrec.date_of_work_inferred > 0 then
      misc = '[' || misc || ']';
    end if;
    
    work_desc = work_desc || misc;
  end if;

  work_desc = work_desc || ')';
  return work_desc;
end;

$$;


