--

CREATE FUNCTION dbf_impact_list_affiliations(input_person_id character varying) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  affiliations text not null := '';
  decoded_row record;
  prev_type integer not null := 0;
begin
  for decoded_row in select 
    t.org_type_id, 
    t.org_type_desc,
    s.org_subtype_desc
  from
    impact_org_type t,
    impact_org_subtype s,
    impact_relationship r
  where
    r.left_table_name = 'impact_person'
    and r.left_id_value = input_person_id
    and r.relationship_type = 'had_affiliation_of_type'
    and r.right_table_name = 'impact_org_subtype'
    and r.right_id_value = s.org_subtype_id::varchar
  and
    s.org_type_id = t.org_type_id
  order by
    org_type_desc, org_subtype_desc
  loop
    if decoded_row.org_type_id != prev_type then
      if prev_type = 0 then
        affiliations = '<ul>'; -- start list of primary types
      else
        affiliations = affiliations || '</ul>'      -- end list of previous subtypes
                                    || '</li>';     -- end previous primary type
      end if;
      prev_type = decoded_row.org_type_id;

      affiliations = affiliations || '<li>' || decoded_row.org_type_desc;  -- start new primary type

      affiliations = affiliations || '<ul>';        -- start new list of subtypes
    end if;
    affiliations = affiliations || '<li>' || decoded_row.org_subtype_desc || '</li>';
  end loop;

  if affiliations > '' then
    affiliations = affiliations || '</ul>'; -- end list of previous subtypes
    affiliations = affiliations || '</li>'; -- end previous primary type
    affiliations = affiliations || '</ul>'; -- end list of primary types
  end if;

  return affiliations;
end;

$$;


