--
-- Name: dbf_impact_list_creators(text, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_list_creators(work_id_parm text, html_output integer) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  reltypes record;
  one_role_details text = '';
  all_role_details text = '';
  role_desc varchar(100);

  list_item_marker  varchar(12) not null default '';

  html_list_start_marker varchar(12) not null default dbf_impact_get_constant_value( 'html_list_start_marker' ); -- <ul><li>
  html_list_item_marker  varchar(12) not null default dbf_impact_get_constant_value( 'html_list_item_marker' ); -- </li><li>
  html_list_end_marker   varchar(12) not null default dbf_impact_get_constant_value( 'html_list_end_marker' ); -- </li></ul>

  non_html_list_item_marker  varchar(12) not null default dbf_impact_get_constant_value( 'list_item_marker' ); --  tilde
  alternative_list_item_marker varchar(4) not null default ' -- ';

  reltype_count integer not null default 0;
  has_multiple_authors varchar(1) not null default 'N';
begin
  if html_output = 1 then
    list_item_marker = html_list_item_marker;
  else
    list_item_marker = alternative_list_item_marker;
  end if;

  -- Look at a list of different relationship types
  for reltypes in select * from impact_relationship_type
  where relationship_code in (  'was_author_of_work',
                                'was_copyist_of_work',
                                'was_commentator_on_work',
                                'was_translator_of_work',
                                'was_glossist_of_work' )
  order by relationship_code
  loop
    -- Processing for one relationship type from the list
    one_role_details = '';

    -- Get a list of people with one particular role
    select dbf_impact_list_rels_decoded( 'impact_person',                -- required_table
                                       reltypes.relationship_code,   -- required_reltype 
                                       'impact_work',                  -- known_table  
                                       work_id_parm,                 -- known_id 
                                       html_output )
    into one_role_details;

    -- If there are some relationships of this type...
    if one_role_details > '' then
      reltype_count = reltype_count + 1;

      -- See if the result for this relationship type is a list or a single entry
      if strpos( one_role_details, non_html_list_item_marker ) > 0 
      or strpos( one_role_details, html_list_item_marker     ) > 0 then
        if reltypes.relationship_code = 'was_author_of_work' then 
          has_multiple_authors = 'Y';
        end if;
      end if;

      -- Add a separator between different roles. In HTML mode this starts a new list item.
      if reltype_count > 1 then
        all_role_details = all_role_details || list_item_marker;
      end if;

      -- Add description of roles, though not for authors 
      if reltypes.relationship_code != 'was_author_of_work' then  -- we only need to spell out the less standard roles

        role_desc = initcap( reltypes.desc_right_to_left ); -- e.g. 'Had Commentator'
        if substr( role_desc, 1, 4 ) = 'Had ' then
          role_desc = substr( role_desc, 5 ); -- Change 'Had Commentator' to just 'Commentator'
        end if;
        all_role_details = all_role_details || role_desc || ': ';
      end if;
      all_role_details = all_role_details || one_role_details;
    end if;
  end loop;

  if reltype_count > 1 and html_output = 1 then
    if has_multiple_authors = 'Y' then
      all_role_details = 'Authors: ' || all_role_details;
    end if;
    all_role_details = html_list_start_marker || all_role_details || html_list_end_marker;
  end if;

  return all_role_details;
end;


$$;


