-- Name: dbf_impact_list_manif_people(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_list_manif_people(input_manif_id character varying) RETURNS text
    LANGUAGE plpgsql
    AS $$

declare
  summary text = '';
  decode text = '';
  relrow record;
  other_table varchar(100) not null = '';
  other_id_value varchar(100) not null = '';
  last_reltype varchar(50) not null = '';
  reldesc varchar(200) not null = '';
  first_char varchar(1) not null = '';

  list_start_marker varchar(12) not null default dbf_impact_get_constant_value( 'html_list_start_marker' );
  list_item_marker  varchar(12) not null default dbf_impact_get_constant_value( 'html_list_item_marker'  );
  list_end_marker   varchar(12) not null default dbf_impact_get_constant_value( 'html_list_end_marker'   );

  reltype_count integer not null default 0;
  entries_for_one_reltype text not null default '';
begin
  for relrow in 
    select rel.*, typ.desc_left_to_right as type_desc 
    from 
      impact_relationship rel, impact_relationship_type typ
    where 
      rel.left_id_value = input_manif_id
      and rel.left_table_name = 'impact_manifestation'
      and rel.right_table_name = 'impact_person'
    and
      rel.relationship_type = typ.relationship_code

    union select rel.*, typ.desc_right_to_left as type_desc
    from 
      impact_relationship rel, impact_relationship_type typ
    where 
      rel.right_id_value = input_manif_id
      and rel.right_table_name = 'impact_manifestation'
      and rel.left_table_name = 'impact_person'
    and
      rel.relationship_type = typ.relationship_code

    order by 
      type_desc, relationship_type, relationship_valid_from, relationship_valid_till, relationship_id
  loop
    ------------------- start loop through results --------------------------------------

    if relrow.left_id_value = input_manif_id then
      other_table = relrow.right_table_name;
      other_id_value = relrow.right_id_value;
    else
      other_table = relrow.left_table_name;
      other_id_value = relrow.left_id_value;
    end if;

    ------------------------------------
    -- *EITHER* new relationship type...
    ------------------------------------
    if relrow.type_desc != last_reltype then
      reltype_count = reltype_count + 1;
      if reltype_count > 1 then
        -------------------------------------------------------------------------------------------------
        -- Add the ENTRY for the PREVIOUS relationship type to the summary.
        -- Before adding it, see if the entry for this relationship type is actually a *list* of entries.
        -- If so, add the list start/end markers before and after the data for that relationship type.
        -------------------------------------------------------------------------------------------------
        if strpos( entries_for_one_reltype, list_item_marker ) > 0 then
          entries_for_one_reltype = list_start_marker || entries_for_one_reltype || list_end_marker;
        end if;
        summary = summary || entries_for_one_reltype;
        entries_for_one_reltype = '';
        -------------------------------------------------------------------------------------
        -- You have now got multiple different relationship types, so add list-item separator
        -------------------------------------------------------------------------------------
        summary = summary || list_item_marker;
      end if;

      -------------------------------------------------------------------
      -- Add the DESCRIPTION of the NEXT relationship type to the summary
      -------------------------------------------------------------------
      reldesc = lower( relrow.type_desc );
      first_char = substr( reldesc, 1, 1 );
      first_char = upper( first_char );
      reldesc = first_char || substr( reldesc, 2 );
      summary = summary || reldesc || ': ';

    ----------------------------------------------------
    -- *OR* another entry for the same relationship type
    ----------------------------------------------------
    else
      entries_for_one_reltype = entries_for_one_reltype || list_item_marker;
    end if;

    -------------------------------------------------------------------------
    -- Add the details to the string holding details of ONE relationship type
    -------------------------------------------------------------------------
    decode = dbf_impact_decode( other_table, other_id_value );

    if relrow.relationship_valid_from is not null and relrow.relationship_valid_till is not null then

      if to_char( relrow.relationship_valid_from, 'YYYY' ) = to_char( relrow.relationship_valid_till, 'YYYY' ) then
        decode = to_char( relrow.relationship_valid_from, 'YYYY' ) || ': ' || decode;
      else
        decode = to_char( relrow.relationship_valid_from, 'YYYY' )
              || '-'
              || to_char( relrow.relationship_valid_till, 'YYYY' )
              || ': '
              || decode;
      end if;

    elseif relrow.relationship_valid_from is not null then
      decode = 'From ' || to_char( relrow.relationship_valid_from, 'YYYY' ) || ': ' || decode;

    elseif relrow.relationship_valid_till is not null then
      decode = 'Until ' || to_char( relrow.relationship_valid_till, 'YYYY' ) || ': ' || decode;
    end if;

    entries_for_one_reltype = entries_for_one_reltype || decode;

    ----------------------------------------------------------
    -- See if there are any alternative names, titles or roles
    ----------------------------------------------------------
    decode = '';

    select skos_altlabel into decode
    from impact_person
    where person_id = other_id_value;

    if decode > '' then
      decode = '; also known as ' || decode;
      entries_for_one_reltype = entries_for_one_reltype || decode;
    end if;

    ----

    decode = '';

    select person_aliases into decode
    from impact_person
    where person_id = other_id_value;

    if decode > '' then
      decode = '; title(s) or role(s): ' || decode;
      entries_for_one_reltype = entries_for_one_reltype || decode;
    end if;


    last_reltype = relrow.type_desc;
    ------------------- end loop through results --------------------------------------
  end loop;

  --------------------------------------
  -- Process the final relationship type
  --------------------------------------
  if strpos( entries_for_one_reltype, list_item_marker ) > 0 then
    entries_for_one_reltype = list_start_marker || entries_for_one_reltype || list_end_marker;
  end if;
  summary = summary || entries_for_one_reltype;

  if reltype_count > 1 then
    summary = list_start_marker || summary || list_end_marker;
  end if;

  return summary;
end;

$$;


