    select person_aliases into decode
    from impact_person
    where person_id = other_id_value;

    if decode > '' then
      decode = '; title(s) or role(s): ' || decode;
      entries_for_one_reltype = entries_for_one_reltype || decode;
    end if;


    last_reltype = relrow.type_desc;
    ------------------- end loop through results --------------------------------------
  end loop;

  --------------------------------------
  -- Process the final relationship type
  --------------------------------------
  if strpos( entries_for_one_reltype, list_item_marker ) > 0 then
    entries_for_one_reltype = list_start_marker || entries_for_one_reltype || list_end_marker;
  end if;
  summary = summary || entries_for_one_reltype;

  if reltype_count > 1 then
    summary = list_start_marker || summary || list_end_marker;
  end if;

  return summary;
end;

$$;


