    select desc_right_to_left, relationship_type, 'left' as other_side
    from impact_relationship, impact_relationship_type
    where right_table_name = 'impact_person'
    and right_id_value = input_person_id
    and left_table_name = other_tab
    and relationship_type = relationship_code
  order by
    reltype_desc, relationship_type
  loop
    -----------------------------------------------------------------------------
    -- You might want either to exclude affiliations or include only affiliations
    -----------------------------------------------------------------------------
    if other_tab = 'impact_person' and result_column != 'affiliations' then
      if relrow.relationship_type = 'member_of' then -- will do 'Affiliations' as a separate column
        continue;
      end if;
    elsif result_column = 'affiliations' then
      if relrow.relationship_type != 'member_of' then
        continue;
      end if;
    end if;

    ---------------------------------------------------------------------------------
    -- You might want either to exclude works authored or include only works authored
    ---------------------------------------------------------------------------------
    if other_tab = 'impact_work' and result_column = 'works_written' then -- include only author-type roles
      if relrow.relationship_type not in ( 'was_author_of_work', 
                                           'was_copyist_of_work', 
                                           'was_commentator_on_work',
                                           'was_translator_of_work', 
                                           'was_glossist_of_work' ) then
        continue;
      end if;
    elsif other_tab = 'impact_work' and result_column = 'associated_works' then -- exclude author-type roles
      if relrow.relationship_type in ( 'was_author_of_work', 
                                       'was_copyist_of_work', 
                                       'was_commentator_on_work',
                                       'was_translator_of_work', 
                                       'was_glossist_of_work' ) then
        continue;
      end if;
    end if;

    -------------------------------------------------------------------------------------
    -- Person-to-person relationships involve extra trickiness because you might have
    -- e.g. a parent-child relationship where this person is the child AND a parent-child
    -- relationship where this person is the parent.
    -------------------------------------------------------------------------------------
    if other_tab = 'impact_person' then
      if relrow.other_side = 'left' then
        decode = dbf_impact_list_lefthand_rels_decoded( other_tab,                -- required_table
                                                      relrow.relationship_type, -- required_reltype
                                                      'impact_person',            -- known_table
                                                      input_person_id,          -- known_id
                                                      1,                        -- html_output
                                                      expand_details );
      else
        decode = dbf_impact_list_righthand_rels_decoded( other_tab,                -- required_table
                                                       relrow.relationship_type, -- required_reltype
                                                       'impact_person',            -- known_table
                                                       input_person_id,          -- known_id
                                                       1,                        -- html_output
                                                       expand_details );
      end if;

    else
      decode = dbf_impact_list_rels_decoded( other_tab,                -- required_table
                                           relrow.relationship_type, -- required_reltype
                                           'impact_person',            -- known_table
                                           input_person_id,          -- known_id
                                           1,                        -- html_output
                                           expand_details );
    end if;

    if decode > '' then
      reltype_count = reltype_count + 1;

      reldesc = upper( substr( relrow.reltype_desc, 1, 1 )) || lower( substr( relrow.reltype_desc, 2 ));
      decode = reldesc || ': ' || decode;

      if reltype_count > 1 then
        summary = summary || html_list_item_marker;
      end if;

      summary = summary || decode;
    end if;
  end loop;

  if reltype_count > 1 then
    summary = html_list_start_marker || summary || html_list_end_marker;
  end if;

  return summary;
end;

$$;


