CREATE FUNCTION dbf_impact_primary_key(the_table_name character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$

declare
  collection_prefix constant varchar(12) = 'impact_';
  prefix_length smallint;
  key_name varchar(100);
begin
    key_name = '';

    if the_table_name = collection_prefix || 'queryable_work' then
      key_name = 'work_id';

