CREATE FUNCTION dbf_impact_refresh_language_of_manifestation() RETURNS trigger
    LANGUAGE plpgsql
    AS $$

declare
  input_manifestation_id varchar(100) not null = '';
  old_lang text;
  new_lang text;
begin
  if TG_OP = 'DELETE' then
    input_manifestation_id = old.manifestation_id;
  else
    input_manifestation_id = new.manifestation_id;
  end if;

  select language_of_manifestation into old_lang from impact_manifestation where manifestation_id = input_manifestation_id;

  select dbf_impact_get_language_of_manifestation( input_manifestation_id ) into new_lang;

  if coalesce( old_lang, '' ) != coalesce( new_lang, '' ) then
    update impact_manifestation
    set language_of_manifestation = new_lang
    where manifestation_id = input_manifestation_id;
  end if;

  if TG_OP = 'DELETE' then
    return old;
  else
    return new;
  end if;
end;

$$;


