--
-- Name: dbf_impact_refresh_person_summary(character varying, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_refresh_person_summary(input_text_id character varying, input_integer_id integer) RETURNS void
    LANGUAGE plpgsql
    AS $$

declare
  oldrec record;
  new_short_string text;
  new_long_string text;
  new_sent integer := 0;
  new_recd integer := 0;
  new_all_works integer := 0;
  new_mentioned integer;
  new_author_of integer;
  has_changed varchar(1) not null := 'N';
  newline constant varchar(1) := E'\n';
begin
  select * into oldrec
  from impact_person_summary
  where iperson_id = input_integer_id;

  ------------------------------------------------------
  -- Get associated people, places and related resources
  ------------------------------------------------------
  select dbf_impact_person_summary( input_text_id,
                                        0, -- don't include works
                                        0  -- don't expand details
                                      ),

         dbf_impact_person_summary( input_text_id,
                                        0, -- don't include works
                                        1  -- do expand details
                                      )
  into new_short_string, new_long_string;

  if coalesce( new_long_string, '' ) != coalesce( oldrec.other_details_summary_searchable, '' ) 
  or coalesce( new_short_string, '' ) != coalesce( oldrec.other_details_summary, '' ) 
  then
    has_changed = 'Y';
  end if;

  if has_changed = 'Y' then
    update impact_person_summary 
    set other_details_summary = new_short_string,
        other_details_summary_searchable = new_long_string
    where iperson_id = input_integer_id;
  end if;

  -------------------------------------
  -- Get numeric total of works written
  -------------------------------------
  select coalesce( count(*), 0) 
  into new_author_of
  from impact_person_author_view va 
  where va.person_id = input_text_id;

  if oldrec.author_of != new_author_of then
    update impact_person_summary 
    set author_of = new_author_of
    where iperson_id = input_integer_id;
  end if;

  -------------------
  -- Get affiliations
  -------------------

  -- Affiliations overview
  new_long_string = '';
  new_long_string = dbf_impact_list_affiliations( input_text_id );
  if new_long_string > '' and new_long_string not like '%</ul>' then
    new_long_string = new_long_string || newline || newline ;
  end if;

  update impact_person_summary 
  set affiliations = coalesce( new_long_string, '' )
  where iperson_id = input_integer_id;

  -- Affiliation dates and details
  new_long_string = '';
  new_long_string = dbf_impact_list_rels_decoded( 'impact_person',   -- required_table
                                                'member_of',     -- required_reltype 
                                                'impact_person',   -- known_table 
                                                input_text_id,   -- known_id 
                                                1,               -- html_output 
                                                1 );             -- expand_details 
  if new_long_string > '' and new_long_string not like '%</ul>' then
    new_long_string = new_long_string || newline || newline ;
  end if;

  if new_long_string > '' then
    new_long_string = 'Affiliation dates and details: ' || new_long_string;
  end if;

  -- Affiliations dates and details
  update impact_person_summary 
  set affiliations = affiliations || coalesce( new_long_string, '' )
  where iperson_id = input_integer_id;

  -------------
  -- Add nisbas
  -------------
  new_long_string = dbf_impact_list_rels_decoded( 'impact_nisba',    -- required_table
                                                'member_of',     -- required_reltype 
                                                'impact_person',   -- known_table 
                                                input_text_id,   -- known_id 
                                                1,               -- html_output 
                                                1 );             -- expand_details 
  if new_long_string > '' then
    if new_long_string not like '%</ul>' then
      new_long_string = new_long_string || newline || newline ;
    end if;
    new_long_string = 'Nisba(s): ' || new_long_string;
  end if;

  update impact_person_summary 
  set nisba_and_nationality = coalesce( new_long_string, '' )
  where iperson_id = input_integer_id;

  --------------------
  -- Add nationalities
  --------------------
  new_long_string = dbf_impact_list_rels_decoded( 'impact_nationality', -- required_table
                                                'member_of',     -- required_reltype 
                                                'impact_person',   -- known_table 
                                                input_text_id,   -- known_id 
                                                1,               -- html_output 
                                                1 );             -- expand_details 
  if new_long_string > '' then
    new_long_string = 'Nationality: ' || new_long_string;
  end if;

  update impact_person_summary 
  set nisba_and_nationality = nisba_and_nationality || coalesce( new_long_string, '' )
  where iperson_id = input_integer_id;

  ----------------------
  -- Add role categories
  ----------------------
  new_long_string = dbf_impact_list_rels_decoded( 'impact_role_category', -- required_table
                                                'member_of',     -- required_reltype 
                                                'impact_person',   -- known_table 
                                                input_text_id,   -- known_id 
                                                1,               -- html_output 
                                                1 );             -- expand_details 
  update impact_person_summary 
  set role_categories = coalesce( new_long_string, '' )
  where iperson_id = input_integer_id;

  -----------------------
  -- Get works written
  -----------------------
  new_long_string = '';

  new_long_string = dbf_impact_person_summary( input_text_id, 'impact_work', 0, 'works_written' );

  if coalesce( oldrec.works_written, '' ) != coalesce( new_long_string, '' ) then
    update impact_person_summary 
    set works_written = new_long_string
    where iperson_id = input_integer_id;
  end if;


  -----------------------
  -- Get associated works
  -----------------------
  new_long_string = '';

  new_long_string = dbf_impact_person_summary( input_text_id, 'impact_work', 0, 'associated_works' );

  if coalesce( oldrec.associated_works, '' ) != coalesce( new_long_string, '' ) then
    update impact_person_summary 
    set associated_works = new_long_string
    where iperson_id = input_integer_id;
  end if;

  --------------------------------
  -- Get associated manifestations
  --------------------------------
  new_long_string = '';

  new_long_string = dbf_impact_person_summary( input_text_id, 'impact_manifestation', 0 );

  if coalesce( oldrec.associated_manifestations, '' ) != coalesce( new_long_string, '' ) then
    update impact_person_summary 
    set associated_manifestations = new_long_string
    where iperson_id = input_integer_id;
  end if;

  return;
end;

$$;


