--
-- Name: dbf_impact_refresh_queryable_work(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION dbf_impact_refresh_queryable_work(input_work_id character varying) RETURNS void
    LANGUAGE plpgsql
    AS $$

declare
  workrec record;
  new_flags text not null := '';
  misc text;
  rel_rec record;
  checkdate date;
  string_length integer;
  mcount integer not null default 0;
  quoted_text_length constant integer := 200;
  newline constant varchar(1) := E'\n';
begin
  -----------------------------------------------------------
  -- Refresh columns copied from the main 'work' table itself
  -----------------------------------------------------------
  select * from impact_work into workrec where work_id = input_work_id;

  -----------------------------------------------------------------------------
  -- For IMPAcT, Gregorian/Christian date is entered manually rather than being
  -- auto-generated, so we need to check that they have entered it correctly.
  -----------------------------------------------------------------------------
  begin
    checkdate = workrec.date_of_work_std_gregorian::date;
  exception
    when others then
      workrec.date_of_work_std_gregorian = '9999-12-31';
  end;

  update impact_queryable_work set

    description                =    workrec.description                ,
    work_title                 =    workrec.work_title                 ,
    work_alternative_titles    =    workrec.work_alternative_titles                 ,

    date_of_work_as_marked     =    workrec.date_of_work_as_marked     ,
    date_of_work_std           =    workrec.date_of_work_std::date     ,
    date_of_work_inferred      =    workrec.date_of_work_inferred      ,
    date_of_work_uncertain     =    workrec.date_of_work_uncertain     ,
    date_of_work_approx        =    workrec.date_of_work_approx        ,

    christian_date             =    workrec.date_of_work_std_gregorian::date,

    authors_as_marked          =    workrec.authors_as_marked          ,
    authors_inferred           =    workrec.authors_inferred           ,
    authors_uncertain          =    workrec.authors_uncertain          ,

    addressees_as_marked       =    workrec.addressees_as_marked       ,
    addressees_inferred        =    workrec.addressees_inferred        ,
    addressees_uncertain       =    workrec.addressees_uncertain       ,

    origin_as_marked           =    workrec.origin_as_marked           ,
    origin_inferred            =    workrec.origin_inferred            ,
    origin_uncertain           =    workrec.origin_uncertain           ,

    destination_as_marked      =    workrec.destination_as_marked      ,
    destination_inferred       =    workrec.destination_inferred       ,
    destination_uncertain      =    workrec.destination_uncertain      ,

    language_of_work           =    workrec.language_of_work           ,
    work_is_translation        =    workrec.work_is_translation        ,

    accession_code             =    workrec.accession_code             ,
    work_to_be_deleted         =    workrec.work_to_be_deleted         ,
    edit_status                =    workrec.edit_status
  where
    work_id = input_work_id;

  ------------------------------------------------------------------------------------------

  -- Year of work copied from main 'work' table
  update impact_queryable_work set date_of_work_std_year =
    case 
      when workrec.date_of_work_std_is_range > 0 and ( workrec.date_of_work2_std_year is not null or
                                                       workrec.date_of_work2_std_month is not null or
                                                       workrec.date_of_work2_std_day is not null ) 
      then
        workrec.date_of_work2_std_year
      else
        workrec.date_of_work_std_year
    end
  where work_id = input_work_id;

  -- Month of work copied from main 'work' table
  update impact_queryable_work set date_of_work_std_month =
    case 
      when workrec.date_of_work_std_is_range > 0 and ( workrec.date_of_work2_std_year is not null or
                                                       workrec.date_of_work2_std_month is not null or
                                                       workrec.date_of_work2_std_day is not null ) 
      then
        workrec.date_of_work2_std_month
      else
        workrec.date_of_work_std_month
    end
  where work_id = input_work_id;

  -- Day of work copied from main 'work' table
  update impact_queryable_work set date_of_work_std_day =
    case 
      when workrec.date_of_work_std_is_range > 0 and ( workrec.date_of_work2_std_year is not null or
                                                       workrec.date_of_work2_std_month is not null or
                                                       workrec.date_of_work2_std_day is not null ) 
      then
        workrec.date_of_work2_std_day
      else
        workrec.date_of_work_std_day
    end
  where work_id = input_work_id;

  ------------------------------------------------------------------------------------------
  -- Type of work
  update impact_queryable_work
  set type_of_work = ( select t.work_type_desc 
                       from impact_work_type t, impact_work w 
                       where t.work_type_id = w.work_type
                       and w.work_id = input_work_id )
  where work_id = input_work_id;

  ------------------------------------------------------------------------------------------
  ------------------------------------
  -- Summarise relationships with work
  ------------------------------------
  -- Creators
  -- Searchable version of creators

  update impact_queryable_work 
  set creators_searchable = ''
  where work_id = input_work_id;

  -- For the searchable version, add all the person's synonyms and aliases
  select dbf_impact_decode_work_role_with_aliases( 'was_author_of_work', input_work_id ) into misc;
  if misc > '' then
    update impact_queryable_work 
    set creators_searchable = creators_searchable || newline || misc
    where work_id = input_work_id;
  end if;

  select dbf_impact_decode_work_role_with_aliases( 'was_copyist_of_work', input_work_id ) into misc;
  if misc > '' then
    update impact_queryable_work 
    set creators_searchable = creators_searchable || newline || misc
    where work_id = input_work_id;
  end if;

  select dbf_impact_decode_work_role_with_aliases( 'was_commentator_on_work', input_work_id ) into misc;
  if misc > '' then
    update impact_queryable_work 
    set creators_searchable = creators_searchable || newline || misc
    where work_id = input_work_id;
  end if;

  select dbf_impact_decode_work_role_with_aliases( 'was_translator_of_work', input_work_id ) into misc;
  if misc > '' then
    update impact_queryable_work 
    set creators_searchable = creators_searchable || newline || misc
    where work_id = input_work_id;
  end if;

  select dbf_impact_decode_work_role_with_aliases( 'was_glossist_of_work', input_work_id ) into misc;
  if misc > '' then
    update impact_queryable_work 
    set creators_searchable = creators_searchable || newline || misc
    where work_id = input_work_id;
  end if;
  ------------------------------------------------------------------------------------------

  -- Displayed version of creators
  -- already has HTML list start/item/end markers in the string passed back

  update impact_queryable_work 
  set creators_for_display = dbf_impact_list_creators_for_display( input_work_id )
  where work_id = input_work_id;
  ------------------------------------------------------------------------------------------

  -- Addressees are not very relevant to IMPAcT so don't need to be in search/results pages.
  -- However will re-use the column for people and places associated with manifestation.
  -- In the view will call this column 'people_and_places_associated_with_manifestations'.

  update impact_queryable_work
  set addressees_searchable = ''
  where work_id = input_work_id;

  for rel_rec in select left_id_value from impact_relationship
  where left_table_name = 'impact_manifestation'
  and relationship_type = 'is_manifestation_of'
  and right_table_name = 'impact_work'
  and right_id_value = input_work_id
  order by left_id_value

  loop
    ---------------------------------------
    -- People associated with manifestation
    ---------------------------------------
    misc = '';
    select dbf_impact_list_manif_people( rel_rec.left_id_value ) into misc;
    if misc > '' then
      update impact_queryable_work 
      set addressees_searchable = addressees_searchable || newline || misc
      where work_id = input_work_id;
    end if;

    ---------------------------------------
    -- Places associated with manifestation
    ---------------------------------------
    misc = '';
    select dbf_impact_list_rels_decoded( 'impact_location',       --required_table
                                       '%',                   --required_reltype i.e. any
                                       'impact_manifestation',  --known_table
                                       rel_rec.left_id_value, --known_id
                                       1,                     --html_output 
                                       1                      --expand details
                                     ) into misc;
    if misc > '' then
      update impact_queryable_work
      set addressees_searchable = addressees_searchable || newline || misc
      where work_id = input_work_id;
    end if;
  end loop;

  ------------------------------------------------------------------------------------------
  -- Place of composition
  update impact_queryable_work 
  set places_from_searchable = dbf_impact_list_origins_searchable( input_work_id )
  where work_id = input_work_id;

  update impact_queryable_work 
  set places_from_for_display = dbf_impact_list_origins_for_display( input_work_id )
  where work_id = input_work_id;
  ------------------------------------------------------------------------------------------

  -- Destinations no longer needed - not relevant to IMPAcT

  ------------------------------------------------------------------------------------------
  -- People mentioned and other people associated with work 

  misc = '';
  select dbf_impact_list_related_people( input_work_id ) into misc;

  -- If there are multiple people, they will already be in an HTML list.
  update impact_queryable_work 
  set people_mentioned = misc
  where work_id = input_work_id;

  ------------------------------------------------------------------------------------------

  -- Places mentioned.
  -- These can go in with people.

  misc = '';

  select dbf_impact_list_rels_decoded( 'impact_location',       --required_table
                                     'mentions_place',      --required_reltype
                                     'impact_work',           --known_table
                                     input_work_id,         --known_id
                                     1,                     --html_output 
                                     1                      --expand details
                                   ) into misc;
  if misc > '' then
    -- If there are multiple places, they will already be in an HTML list.
    misc = '<strong>Was associated with places:</strong> ' || misc;

    update impact_queryable_work set
      people_mentioned = people_mentioned || newline || newline
    where
      people_mentioned > ''
    and
      work_id = input_work_id;

    update impact_queryable_work set
      people_mentioned = coalesce( people_mentioned, '' ) || misc
    where
      work_id = input_work_id;
  end if;

  misc = '';
  
  -----------------------------------------------------------------------------------------------
  -- Related works

  select dbf_impact_list_related_works( input_work_id ) 
  into misc; -- the 'related works' string now comes back as a fully-formatted HTML list

  update impact_queryable_work 
  set related_works = misc
  where work_id = input_work_id;

  misc = '';
  
  -----------------------------------------------------------------------------------------------
  -- Shelfmarks/manifestations

  select dbf_impact_full_manifestation_details( input_work_id ) into misc;

  update impact_queryable_work 
  set 
    manifestations_for_display = misc,
    manifestations_searchable = dbf_impact_convert_list_from_html( misc )
  where work_id = input_work_id;

  misc = '';

  select count(*) into mcount
  from impact_relationship
  where left_table_name = 'impact_manifestation'
  and relationship_type = 'is_manifestation_of'
  and right_table_name = 'impact_work'
  and right_id_value = input_work_id;

  update impact_queryable_work 
  set 
    manif_count = mcount
  where work_id = input_work_id;

  ------------------------------------------------------------------------------------------

  -- Notes on work
  -- Judith does not want these in the summary

  -- Notes on authors/senders
  -- ditto

  -------------------------------------
  -- Add expanded display of flags 
  -------------------------------------
  if workrec.date_of_work_inferred > 0 or workrec.date_of_work_uncertain > 0 or workrec.date_of_work_approx > 0 
  then
    if workrec.date_of_work_inferred > 0 then
      new_flags := new_flags || 'Date of work INFERRED. ';
    end if;
    if workrec.date_of_work_uncertain > 0 then
      new_flags := new_flags || 'Date of work UNCERTAIN. ';
    end if;
    if workrec.date_of_work_approx > 0 then
      new_flags := new_flags || 'Date of work APPROXIMATE. ';
    end if;
    if workrec.date_of_work_as_marked > '' then
      new_flags := new_flags || '(Date of work as marked: ' || workrec.date_of_work_as_marked || ') ';
    end if;
  end if;

  if workrec.authors_inferred > 0 or workrec.authors_uncertain > 0 then
    if new_flags > '' then
      new_flags := new_flags || ' ~ ';
    end if;

    if workrec.authors_inferred > 0 then
      new_flags := new_flags || 'Author INFERRED. ';
    end if;
    if workrec.authors_uncertain > 0 then
      new_flags := new_flags || 'Author UNCERTAIN. ';
    end if;

    if workrec.authors_as_marked > '' then
      new_flags := new_flags || '(Author as marked: ' || workrec.authors_as_marked || ')';
    end if;
  end if;

  if workrec.origin_inferred > 0 or workrec.origin_uncertain > 0 then
    if new_flags > '' then
      new_flags := new_flags || ' ~ ';
    end if;

    if workrec.origin_inferred > 0 then
      new_flags := new_flags || 'Place of composition INFERRED. ';
    end if;
    if workrec.origin_uncertain > 0 then
      new_flags := new_flags || 'Place of composition UNCERTAIN. ';
    end if;

    if workrec.origin_as_marked > '' then
      new_flags := new_flags || '(Place of composition as marked: ' || workrec.origin_as_marked || ')';
    end if;
  end if;

  if workrec.title_of_work_inferred > 0 or workrec.title_of_work_uncertain > 0 or workrec.title_of_work_unknown > 0
  then
    if new_flags > '' then
      new_flags := new_flags || ' ~ ';
    end if;

    if workrec.title_of_work_inferred > 0 then
      new_flags := new_flags || 'Title of work INFERRED. ';
    end if;
    if workrec.title_of_work_uncertain > 0 then
      new_flags := new_flags || 'Title of work UNCERTAIN. ';
    end if;
    if workrec.title_of_work_unknown > 0 then
      new_flags := new_flags || 'Title of work UNKNOWN. ';
    end if;
  end if;

  update impact_queryable_work 
  set flags = new_flags
  where coalesce( flags, '' ) != new_flags
  and work_id = input_work_id;
  ------------------------------------------------------------------------------------------

  ---------------------------------
  -- Add links to related resources
  ---------------------------------
  misc = '';
  select dbf_impact_list_work_resources_brief( input_work_id ) into misc;

  -- The 'related resources' string comes back as a non-HTML list, separated by tildes.
  if misc > '' then
    misc = dbf_impact_convert_list_to_html( misc );
  end if;

  update impact_queryable_work 
  set related_resources = misc
  where work_id = input_work_id;

  misc = '';
  ------------------------------------------------------------------------------------------

  ---------------------------------------
  -- Add links to images of manifestation
  ---------------------------------------
  update impact_queryable_work 
  set images = dbf_impact_list_images_of_work( input_work_id )
  where work_id = input_work_id;
  ------------------------------------------------------------------------------------------

  -----------------------
  -- Add subjects of work
  -----------------------
  update impact_queryable_work 
  set subjects = dbf_impact_list_subjects_of_work( input_work_id )
  where work_id = input_work_id;

  ------------------------------------------------------------------------------------------

  -------------------------------------------------
  -- Add incipit and explicit (in 'abstract' field)
  -------------------------------------------------
  -- Incipit
  ----------
  misc = coalesce( workrec.incipit, '' );
  if misc > '' then

    -- Include up to 200 characters
    string_length = length( misc );
    if string_length > quoted_text_length then
      misc = substr( misc, 1, quoted_text_length );

      -- Try to break on a space
      if strpos( misc, ' ' ) > 0 then
        for i in reverse length( misc )..1 loop
          if substr( misc, i, 1 ) = ' ' then
            misc = substr( misc, 1, i );
            exit;
          end if;
        end loop;
      end if;
    end if;
    misc = 'Incipit: ' || misc;
  end if;

  update impact_queryable_work 
  set abstract = misc
  where work_id = input_work_id;

  -----------
  -- Explicit
  -----------
  misc = coalesce( workrec.explicit, '' );
  if misc > '' then

    -- Include up to 200 characters
    string_length = length( misc );
    if string_length > quoted_text_length then
      misc = substr( misc, 1, quoted_text_length );

      -- Try to break on a space
      if strpos( misc, ' ' ) > 0 then
        for i in reverse length( misc )..1 loop
          if substr( misc, i, 1 ) = ' ' then
            misc = substr( misc, 1, i );
            exit;
          end if;
        end loop;
      end if;
    end if;
    misc = 'Explicit: ' || misc;

    if workrec.incipit > '' then
      misc = newline || newline || misc;
    end if;
  end if;

  update impact_queryable_work 
  set abstract = coalesce( abstract, '' ) || misc
  where work_id = input_work_id;

  ------------------------------------------------------------------------------------------
  return;
end;

$$;


