CREATE FUNCTION dbf_impact_save_session_data(input_username text, old_session text, new_session text) RETURNS integer
    LANGUAGE plpgsql STRICT SECURITY DEFINER
    AS $$

declare
  failure constant integer := 0;
  success constant integer := 1;

  rows_affected integer := 0;
begin
  if old_session is null or old_session = '' then

    insert into impact_sessions (username, session_code)
    values (input_username, new_session );
  else
    update impact_sessions 
    set 
      session_timestamp = current_timestamp
    where
      username = input_username
      and session_code = old_session;
  end if;

  get diagnostics rows_affected = row_count;
  if rows_affected != 1 then
    return failure;
  end if;

  delete from impact_sessions where session_timestamp < current_date;

  return success;
end;

$$;


